pragma solidity ^0.5.10;

contract StringIndexOf{
    function indexOf(address[] memory haystack, address needle) internal pure returns(uint){
        uint index = uint(-1);
        uint haystackLength = haystack.length;
        for(uint i = 0; i < haystackLength; i++){
            if (haystack[i] == needle){
                index = i;
                break;
            }
        }

        return index;
    }
}
