pragma solidity ^0.5.10;

import "./ValidationChecker.sol";
import "./ERC20.sol";

contract TokenClaimer is ValidationChecker {

    address public admin;

    event Claim(address indexed token, address indexed user, uint256 value);

    mapping (bytes32 => uint) public signedMessages;

    function claim(address token, uint amount, uint salt, bytes32 hash, uint8 v, bytes32 r, bytes32 s) public returns(bool)
    {
        assert(signedMessages[hash] == 0); // messages hasnt been processed
        assert(hash == getClaimHash(token, msg.sender, amount, salt)); // details and hash match
        assert(isValidSignature(admin, hash, v,r,s)); // admin signed this message
        signedMessages[hash] = block.number;
        assert(ERC20(token).mint(msg.sender, amount));
        emit Claim(token, msg.sender, amount);
        return true;
    }

    function getClaimHash(address token, address claimant, uint amount, uint salt) public pure returns (bytes32 _hash)
    {
        return keccak256(abi.encodePacked("CLAIM", token, claimant, amount, salt));
    }

}
